import Counter from './components/Counter';
import Header from './components/Header';
import Auth from './components/Auth';
import { useSelector } from 'react-redux';


function App() {

  const auth = useSelector(state => state.auth.isAuthenticated);

  return (
    <>
      <Header />
      if 
      {!auth && <Auth />}
      <Counter />
    </>

  );
}

export default App;
